import React from "react";
import { Column, Row } from "simple-flexbox";
import CustomInput from "../../common/components/CustomInput";
import styled from "styled-components";

const MainComponent = styled.div`
  display: flex;
  justify-content: space-between;
  ${'' /* max-width: 1920px;
  width: 100%;
  height: 100%; */}
`;


const LoginPage = styled.div`
`;

const LoginPageSlider = styled.div`
`;

const Form = styled.div`
`;

const SignFont = styled.div``;

const Remember = styled.div``;
// const IconButton = styled.div``;

function LoginForm(props) {
  let { state, onChangeEvent, onLoginClicked } = props;
  return (
    <>
      <MainComponent>
        <LoginPage>
          <Form>
              <div className="loginheading">
                <h3>Login to</h3>
                <img src="/images/materiallibrarylogo.png" alt="logo" width={220} />
              </div>
              <p>One Stop Solution For Contruction World</p>
              <div className="logininputs">
                <h2>Username</h2>
                <CustomInput
                  id="email"
                  type="text"
                  placeholder="Email"
                  value={state ? state.email : ""}
                  onChange={onChangeEvent}
                  error={state ? state.emailError : ""}
                  className="input-box"
                />
                <h2>Password</h2>
                <CustomInput
                  id="password"
                  placeholder="Passoword"
                  type={state && state.isPasswordVisible ? "text" : "password"}
                  value={state ? state.password : ""}
                  onChange={onChangeEvent}
                  error={state ? state.passwordError : ""}
                  className="input-box"
                />
              </div>
              <div className="remandforgot">
                <label htmlFor="">
                  <input type="checkbox" />
                  Remember e
                </label>
                

              </div>

          </Form>
        </LoginPage>
        <LoginPageSlider>
          <div className="loginslider">
            <img src="https://t3.ftcdn.net/jpg/01/22/71/96/360_F_122719641_V0yw2cAOrfxsON3HeWi2Sf4iVxhv27QO.jpg" alt="logo" />
          </div>
        </LoginPageSlider>
      </MainComponent>
    </>
  );
}

export default LoginForm;
