import React from "react";
import { Column, Row } from "simple-flexbox";
import CustomInput from "../../common/components/CustomInput";
import styled from "styled-components";

const MainComponent = styled.div`
  max-width: 1920px;
  width: 100%;
  max-height: 1080px;
  min-height: 700px;
  height: 100vh;
  // background: #ffffff 0% 0% no-repeat center center fixed;
  // background-color: #fff ;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  // padding: 236px 0px 446px 0px;
`;

const LoginComponent = styled.div`
  width: 500px;
  height: 100vh;
  display: flex;
  align-items: start;
  flex-direction: column;
  margin: 0px auto;
`;

const Loginheading = styled.div``;
const SignFont = styled.div`
  
`;
const Button = styled.button`
  
`;
const ForgotFont = styled.div`
  
`;
const Icon = styled.img`
  
`;

const Remember = styled.div``;
// const IconButton = styled.div``;

function LoginForm(props) {
  let { state, onChangeEvent, onLoginClicked } = props;
  return (
    <MainComponent>
      <LoginComponent>
        <Loginheading>Login to <span><strong className="" style={{color: "#6c6c6c"}}>material</strong></span>
        <span>
          <strong className="" style={{color: "#fb4404"}}>library</strong>
        </span>
        </Loginheading>
        <p className="mb-4">One Stop Solution For Construction World</p>
        <Column>
          <form onSubmit={onLoginClicked}>
            <h2>Username</h2>
            <CustomInput
              id="email"
              type="text"
              placeholder="your-email@gmail.com"
              value={state ? state.email : ""}
              onChange={onChangeEvent}
              error={state ? state.emailError : ""}
              className="input-box"
            />
            <h2>Password</h2>
            <CustomInput
              id="password"
              placeholder="Your Passoword"
              type={state && state.isPasswordVisible ? "text" : "password"}
              value={state ? state.password : ""}
              onChange={onChangeEvent}
              error={state ? state.passwordError : ""}
              className="input-box"
            />
            
            <div className="d-flex"><input type="checkbox"/>Remember me 
            <ForgotFont>Forgot Password?</ForgotFont>
            </div>
            <Button type="submit">Log in</Button>
          </form>
        </Column>
      </LoginComponent>
      <img src="https://www.kajariaceramics.com/blog/images/pavigres_1.jpg" alt="tiles" />
    </MainComponent>
  );
}

export default LoginForm;
